import React, { useState, useEffect } from 'react';
import styles from './Autocomplete.module.css';
import debounce from '../utils/debounce';

export interface Option {
    value: string;
    label: string;
}

interface AutocompleteProps {
    description: string;
    options: Option[];
    placeholder: string;
    disabled: boolean;
    filterOptions?: (options: Option[], inputValue: string) => Option[];
    loading: boolean;
    multiple: boolean;
    onChange: (selected: Option[]) => void;
    onInputChange: (inputValue: string) => void;
    renderOption: (option: Option) => React.ReactNode;
    value: Option | Option[];
    searchMode: 'local' | 'async';
    asyncSearch?: (query: string) => Promise<Option[]>;
}

const Autocomplete: React.FC<AutocompleteProps> = ({
    description,
    options,
    placeholder,
    disabled,
    filterOptions,
    loading,
    multiple,
    onChange,
    onInputChange,
    renderOption,
    value,
    searchMode,
    asyncSearch,
}) => {
    const [inputValue, setInputValue] = useState('');
    const [filteredOptions, setFilteredOptions] = useState<Option[]>([]);
    const [selectedOptions, setSelectedOptions] = useState<Option[]>([]);
    const [showOptions, setShowOptions] = useState(false); // State to manage showing options

    const handleAsyncSearch = debounce(async (query: string) => {
        if (asyncSearch) {
            const results = await asyncSearch(query);
            setFilteredOptions(results);
        }
    }, 50);

    useEffect(() => {
        if (searchMode === 'async' && asyncSearch) {
            handleAsyncSearch(inputValue);
        } else if (filterOptions) {
            setFilteredOptions(filterOptions(options, inputValue));
        } else {
            setFilteredOptions(options.filter(option =>
                option.label.toLowerCase().includes(inputValue.toLowerCase())
            ));
        }
    }, [inputValue, options, filterOptions, searchMode, asyncSearch, handleAsyncSearch]);

    const handleInputChange = (event: React.ChangeEvent<HTMLInputElement>) => {
        setInputValue(event.target.value);
        onInputChange(event.target.value);
        setShowOptions(true); // Show options when input is changed
    };

    const handleFocus = () => {
        setShowOptions(true); // Show options when input is focused
    };

    const handleBlur = () => {
        setShowOptions(false); // Hide options only if search mode is async
        // Hide options when input loses focus
    };

    const handleOptionSelection = (option: Option) => {
        const isSelected = selectedOptions.some(item => item.value === option.value);

        if (isSelected) {
            const updatedOptions = selectedOptions.filter(item => item.value !== option.value);
            setSelectedOptions(updatedOptions);
        } else {
            setSelectedOptions([...selectedOptions, option]);
        }
    };

    useEffect(() => {
        // Update input value when selectedOptions change
        const selectedLabels = selectedOptions.map(opt => opt.label);
        setInputValue(selectedLabels.join(', '));
        onChange(selectedOptions); // Notify parent of selected options change
    }, [selectedOptions, onChange]);

    return (
        <div className={styles.autocomplete} tabIndex={0} onBlur={handleBlur} onFocus={handleFocus}>
            {description && <div className="description">{description}</div>}
            <input
                type="text"
                value={inputValue}
                onChange={handleInputChange}
                placeholder={placeholder}
                disabled={disabled}
            />
            {loading && <div className="spinner">Loading...</div>}
            {showOptions && searchMode === 'local' && (
                <div className="autocomplete-options mt-2">
                    {filteredOptions.map(option => (
                        <div
                            key={option.value}
                            className={`p-2 cursor-pointer transition duration-300 ease-out hover:bg-gray-100 ${selectedOptions.some(item => item.value === option.value) ? 'bg-blue-200' : ''
                                }`}
                            onClick={() => handleOptionSelection(option)}
                        >
                            {renderOption(option)}
                        </div>
                    ))}
                </div>
            )}
        </div>
    );
};

export default Autocomplete;


