import React, { useState, useEffect } from 'react';
import styles from './Autocomplete.module.css';
import debounce from '../utils/debounce';

export interface Option {
  value: string;
  label: string;
}

interface AutocompleteProps {
  description: string;
  options: Option[];
  placeholder: string;
  disabled: boolean;
  filterOptions?: (options: Option[], inputValue: string) => Option[];
  label: string;
  loading: boolean;
  multiple: boolean;
  onChange: (selected: Option | Option[]) => void;
  onInputChange: (inputValue: string) => void;
  renderOption: (option: Option) => React.ReactNode;
  value: Option | Option[];
  searchMode: 'local' | 'async';
  asyncSearch?: (query: string) => Promise<Option[]>;
  handleOptionClick: (option: Option) => void;
}

const Autocomplete: React.FC<AutocompleteProps> = ({
  description,
  options,
  placeholder,
  disabled,
  filterOptions,
  label,
  loading,
  multiple,
  onChange,
  onInputChange,
  renderOption,
  value,
  searchMode,
  asyncSearch,
  handleOptionClick,
}) => {
  const [inputValue, setInputValue] = useState('');
  const [filteredOptions, setFilteredOptions] = useState<Option[]>([]);

  const handleAsyncSearch = debounce(async (query: string) => {
    if (asyncSearch) {
      const results = await asyncSearch(query);
      setFilteredOptions(results);
    }
  }, 100);

  useEffect(() => {
    if (searchMode === 'async' && asyncSearch) {
      handleAsyncSearch(inputValue);
    } else if (filterOptions) {
      setFilteredOptions(filterOptions(options, inputValue));
    } else {
      setFilteredOptions(options.filter(option =>
        option.label.toLowerCase().includes(inputValue.toLowerCase())
      ));
    }
  }, [inputValue, options, filterOptions, searchMode, asyncSearch, handleAsyncSearch]);

  const handleInputChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    setInputValue(event.target.value);
    onInputChange(event.target.value);
  };

  const handleFocus = () => {
    setFilteredOptions(options);
  };

  return (
    <div className={styles.autocomplete}>
      <label className={styles.label}>{label}</label>
      <input
        type="text"
        value={inputValue}
        onChange={handleInputChange}
        onFocus={handleFocus}
        placeholder={placeholder}
        disabled={disabled}
      />
      {loading && <div className="spinner">Loading...</div>}
      <div className="autocomplete-options mt-2">
        {filteredOptions.map(option => (
          <div
            key={option.value}
            className="p-2 cursor-pointer transition duration-300 ease-out hover:bg-gray-100"
            onClick={() => handleOptionClick(option)}
          >
            {renderOption(option)}
          </div>
        ))}
      </div>
      {description && <div className="description">{description}</div>}
    </div>
  );
};

export default Autocomplete;
export { };
