import React, { useState, ChangeEvent } from 'react';
import styles from './Autocomplete.module.css';

export type Option= {
    value: string;
    label: string;
}

export interface AutocompleteProps {
    description: string;
    disabled: boolean;
    filterOptions: (options: Option[], inputValue: string) => Option[];
    label: string;
    loading: boolean;
    multiple: boolean;
    onChange: (selected: Option | Option[]) => void;
    onInputChange: (inputValue: string) => void;
    options: Option[];
    placeholder: string;
    renderOption: (option: Option) => React.ReactNode;
    value: Option | Option[];
}

const Autocomplete: React.FC<AutocompleteProps> = ({
    description,
    disabled = false,
    filterOptions,
    label,
    loading = false,
    multiple = false,
    onChange,
    onInputChange,
    options,
    placeholder = 'Search...',
    renderOption,
    value,
}) => {
    const [inputValue, setInputValue] = useState('');

    const handleInputChange = (event: React.ChangeEvent<HTMLInputElement>) => {
        const value = event.target.value;
        setInputValue(value);
        if (onInputChange) {
            onInputChange(value);
        }
    };

    const [isFocused, setIsFocused] = useState(false);
    const handleFocus = () => {
        setIsFocused(true);
    }

    const handleOptionClick = (option: Option) => {
        if (multiple) {
            // Logic for multiple selection (e.g., push to array)
        } else {
            onChange(option);
        }
    };

    const filteredOptions = filterOptions(options, inputValue);

    return (
        <div className={`w-full max-w-md mx-auto p-4 border rounded bg-white shadow-md ${styles.autocomplete}`}>
          <label className={`${styles.label} block mb-2 font-bold`}>Search:</label>
          <input
            type="text"
            value={inputValue}
            onChange={handleInputChange}
            placeholder={placeholder}
            disabled={disabled}
            onFocus={handleFocus}
            className="w-full p-2 border rounded"
          />
          {loading && <div className="spinner">Loading...</div>}
          {isFocused && filteredOptions.length > 0 && (
            <div className="autocomplete-options mt-2 absolute z-10 w-full border rounded bg-white shadow-lg">
            {filteredOptions.map(option => (
              <div
                key={option.value}
                className="p-2 cursor-pointer transition duration-300 ease-out hover:bg-gray-100"
                onClick={() => handleOptionClick(option)}
              >
                {renderOption(option)}
              </div>
            ))}
          </div>
          )}
          {description && <div className="description mt-2">{description}</div>}
        </div>
      );
};



export default Autocomplete;
export { };
