import React, { useState, useEffect } from 'react';
import styles from './Autocomplete.module.css';
import debounce from '../utils/debounce';

export interface Option {
  value: string;
  label: string;
}

interface AutocompleteProps {
  description: string;
  options: Option[];
  placeholder: string;
  disabled: boolean;
  loading: boolean;
  onChange: (selected: Option[]) => void;
  renderOption: (option: Option) => React.ReactNode;
  value: Option[];
  searchMode: 'local' | 'async';
  asyncSearch?: (query: string) => Promise<Option[]>;
}

const Autocomplete: React.FC<AutocompleteProps> = ({
  description,
  options,
  placeholder,
  disabled,
  loading,
  onChange,
  renderOption,
  value,
  searchMode,
  asyncSearch,
}) => {
  const [inputValue, setInputValue] = useState('');
  const [selectedOptions, setSelectedOptions] = useState<Option[]>(value); // Initialize selectedOptions with the prop value
  const [showDropdown, setShowDropdown] = useState(false); // State to manage dropdown visibility

  const handleAsyncSearch = debounce(async (query: string) => {
    // Async search implementation here
  }, 300);

  useEffect(() => {
    if (searchMode === 'async') {
      handleAsyncSearch(inputValue); // Perform async search if search mode is async
    }
    // No need to set filteredOptions here, it will be updated in the handleInputChange handler
  }, [inputValue, searchMode, handleAsyncSearch]);

  const handleInputChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    setInputValue(event.target.value);
    // Local filtering logic here if search mode is local
  };

  const handleOptionSelection = (option: Option) => {
    const isSelected = selectedOptions.some(item => item.value === option.value);

    if (isSelected) {
        // Remove the option from selectedOptions if already selected
        const updatedOptions = selectedOptions.filter(item => item.value !== option.value);
        setSelectedOptions(updatedOptions);
      } else {
        // Add the option to selectedOptions if not already selected
        setSelectedOptions([...selectedOptions, option]);
      }
    // No need to remove option if already selected
  };

  useEffect(() => {
    // Update input value when selectedOptions change
    const selectedLabels = selectedOptions.map(opt => opt.label);
    setInputValue(selectedLabels.join(', '));
    onChange(selectedOptions); // Notify parent of selected options change
  }, [selectedOptions, onChange]);

  const toggleDropdown = () => {
    setShowDropdown(!showDropdown); // Toggle dropdown visibility
  };

  const handleFocus = () => {
    setShowDropdown(true); // Show dropdown when input is focused
  };

  const handleBlur = (event: React.FocusEvent<HTMLDivElement>) => {
    const target = event.relatedTarget as HTMLElement;
    if (!target || !target.classList.contains('dropdown-item')) {
      setShowDropdown(false); // Close dropdown if focus moves outside of it
    }
  };

  return (
    <div className={styles.autocomplete}>
      {description && <div className="description">{description}</div>}
      <div className={styles.autocomplete} onBlur={handleBlur} onClick={handleFocus}>
        <input
          type="text"
          value={inputValue}
          onChange={handleInputChange}
          onClick={toggleDropdown}
          tabIndex={0}
          placeholder={placeholder}
          disabled={disabled}
          readOnly // Prevent user from editing input directly
        />
        {searchMode=='local' && showDropdown && (<div className={`dropdown-menu ${showDropdown ? 'show' : ''}`}>
          {options.map(option => (
            <label key={option.value} className="dropdown-item">
              <input
                type="checkbox"
                tabIndex={0}
                checked={selectedOptions.some(item => item.value === option.value)}
                onChange={() => handleOptionSelection(option)}
              />
              {renderOption(option)}
            </label>
          ))}
        </div>
        )}
      </div>
      {loading && <div className="spinner">Loading...</div>}
    </div>
  );
};

export default Autocomplete;

export { };
