import React, { useState } from 'react';
import './App.css';
import Autocomplete, { Option } from './components/Autocomplete';

const App: React.FC = () => {
  const options = [
    { value: 'apple', label: 'Apple' },
    { value: 'banana', label: 'Banana' },
    { value: 'cherry', label: 'Cherry' },
  ];

  const handleInputChange = (inputValue: string) => {
    console.log('Input changed:', inputValue);
  };

  const handleChange = (selected: Option | Option[]) => {
    console.log('Selected:', selected);
    setValue(selected);
  };

  const handleOptionClick = (option: Option) => {
    console.log('Option clicked:', option);
    if (Array.isArray(value)) {
      setValue([...value, option]);
    } else {
      setValue(option);
    }
  };

  const defaultFilterOptions = (options: Option[], inputValue: string) => {
    return options.filter(option =>
      option.label.toLowerCase().includes(inputValue.toLowerCase())
    );
  };

  const renderOption = (option: Option) => {
    return <div>{option.label}</div>;
  };

  const asyncSearch = async (query: string) => {
    return new Promise<Option[]>((resolve) => {
      setTimeout(() => {
        const filteredOptions = options.filter(option =>
          option.label.toLowerCase().includes(query.toLowerCase())
        );
        resolve(filteredOptions);
      }, 1000);
    });
  };

  const [value, setValue] = useState<Option | Option[]>([]);

  return (
    <div className="App">
      <header className="App-header">
        <h1>Autocomplete Component Example</h1>
      </header>
      <main className="App-main">
        <Autocomplete
          description="Synchronous Search"
          options={options}
          onInputChange={handleInputChange}
          onChange={handleChange}
          placeholder="Search..."
          disabled={false}
          filterOptions={defaultFilterOptions}
          loading={false}
          multiple={false}
          renderOption={renderOption}
          value={value}
          searchMode="local"
          asyncSearch={undefined}
        />
        <Autocomplete
          description="Asynchronous Search"
          options={[]}
          onInputChange={handleInputChange}
          onChange={handleChange}
          placeholder="Search..."
          disabled={false}
          filterOptions={undefined}
          loading={false}
          multiple={false}
          renderOption={renderOption}
          value={value}
          searchMode="async"
          asyncSearch={asyncSearch}
        />
      </main>
    </div>
  );
};

export default App;
